glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
#endif
uniform Properties {
  vec2 resolution;
  vec2 gridSize;
  vec4 colorA;
  vec4 colorB;
};
vec4 getColor(vec2 uv) {
  vec2 offset = mod(resolution, gridSize) / 2.0 / resolution;
  vec2 size = resolution / gridSize;
  vec2 pos = floor((uv - offset) * size) / 2.0;
  return (-fract(pos.x + pos.y) < 0.0) ? colorA : colorB;
}
void main () {
  vec4 color = v_color;
  #if USE_TEXTURE
  vec4 texture_tmp = texture(texture, v_uv0);
  #if CC_USE_ALPHA_ATLAS_texture
      texture_tmp.a *= texture(texture, v_uv0 + vec2(0, 0.5)).r;
  #endif
  #if INPUT_IS_GAMMA
    color.rgb *= (texture_tmp.rgb * texture_tmp.rgb);
    color.a *= texture_tmp.a;
  #else
    color *= texture_tmp;
  #endif
  #endif
  color *= getColor(v_uv0);
  #if USE_BGRA
  gl_FragColor = color.bgra;
  #else
  gl_FragColor = color.rgba;
  #endif
}

